package SingFor;

public class Doublefor3 {
	// 이중for 문 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<10; i++)
		{
			for(int j=0; j<=10-i; j++) {
				System.out.println("");
			}
			for(int k=0; k<=2*i; k++) {
			
			}
			System.out.println("백발백중하는 명사수");
		}
		System.out.println();
	}

}
